function xmlToStringAuto(){if(0==document.getElementById("inputTextArea").value.trim().length)return document.getElementById("outputTextArea").value="",void updateTextAreaFooter();document.getElementById("isAuto").checked&&xmlToString()}function xmlToString(){inputACEEditor.getSession().setMode("ace/mode/xml");try{let t=inputACEEditor.getValue();if(0===t.trim().length)return void showToast("Sorry Input Is Empty","warning");let e=xmlToStringLogic(t);outputACEEditor.setValue(e,1)}catch(t){showToast("Please check the Input:","danger"),console.log(t)}}function xmlToStringLogic(t){return jsonToText((new X2JS).xml_str2json(t))}function jsonToText(t){var e="";if("object"==typeof t)if(t instanceof Array)for(var o in t)e+=jsonToText(t[o]);else for(var o in t){var n=t[o];e+="object"==typeof n?o+"\n"+jsonToText(n):o+" "+jsonToText(n)}else e+=t.toString()+"\n";return e}